mapper.select:

SELECT * 
FROM (
	SELECT * FROM 

	ORDER BY "pk" DESC
) AS termsList 
LIMIT ${pageSize} OFFSET (#{page} - 1) * #{pageSize}


SELECT 	fd_terms_ver AS "termsVer"
FROM 	tb_terms
WHERE 	fk_cp_code = #{cpCode}
		AND fk_terms_code = #{termsCode}
ORDER BY fd_regdate DESC
LIMIT 1

mapper.insert:

INSERT INTO tb_terms(

	fk_cp_code
,	fk_terms_code
,	fd_terms_ver
,	fd_terms_title
,	fd_terms_contents
,	fd_terms_mandatory_yn
,	fd_writer_id
,	fd_writer_ip
,	fd_regdate

)
VALUES(

	#{cpCode}
,	'1303'
,	#{termsVer}
,	#{title}
,	#{termsContents}
,	#{mandatoryYN}
,	#{writerId}
,	#{writerIp}
,	#{regDate}

)

mapper.update:

UPDATE tb_terms SET 

	fd_terms_title				= #{title}
,	fd_terms_contents 			= #{termsContents}
,	fd_terms_mandatory_yn 		= #{mandatoryYN}	
,	fd_modifier_id				= #{modifierId}
,	fd_modifier_ip				= #{modifierIp}
,	fd_update_date				= #{updateDate}			

WHERE pk_terms = #{pk}
